ALTER TABLE `zp_mangosd`.`event_resource` 
CHANGE COLUMN `id` `id` INT(11) UNSIGNED NOT NULL ,
CHANGE COLUMN `event_id` `event_id` INT(11) UNSIGNED NOT NULL ,
CHANGE COLUMN `resource_id` `resource_id` INT(11) UNSIGNED NOT NULL ,
